import React from 'react';
import './SuccessStory.css';
import { Container, Row, Col, Card } from 'react-bootstrap';

function SuccessStory() {
  const story = {
    title: 'Implementación de Gemelos Digitales en la Ciudad',
    subtitle: 'Un caso de éxito en la gestión urbana',
    description: 'En este caso, logramos implementar gemelos digitales para mejorar la gestión urbana y reducir costos operativos. La digitalización precisa de la infraestructura urbana ha permitido una mejor planificación y ejecución de proyectos.',



    media: [
      { type: 'image', url: 'http://suriyaco.ar/images/success1.jpg' },
      { type: 'video', url: 'http://suriyaco.ar/images/success1.mp4', poster: 'http://suriyaco.ar/images/success1_poster.jpg' },
    ],
  };

  return (
    <div className="success-story">
      <Container>
        <Row>
          <Col>
            <h1>{story.title}</h1>
            <h2>{story.subtitle}</h2>
            <p>{story.description}</p>
          </Col>
        </Row>
        <Row>
          {story.media.map((mediaItem, index) => (
            <Col md={6} key={index}>
              {mediaItem.type === 'image' ? (
                <Card className="media-card">
                  <Card.Img variant="top" src={mediaItem.url} />
                </Card>
              ) : (
                <Card className="media-card">
                  <video controls poster={mediaItem.poster}>
                    <source src={mediaItem.url} type="video/mp4" />
                    Your browser does not support the video tag.
                  </video>
                </Card>
              )}
            </Col>
          ))}
        </Row>
      </Container>
    </div>
  );
}

export default SuccessStory;
